import './App.css';
import React, {useState} from 'react';
import axios from "axios";


function WeatherSearch() {
    const [city, setCity] = useState ("");
    const [loaded, setLoaded] = useState(false);
    const [weather, setWeather] = useState ({});

    function displayWeather(response) {
        setLoaded(true);
        setWeather({
            temperature: response.data.main.temp,
            wind: response.data.wind.speed,
            humidity: response.data.main.humidity,
            icon: `http://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
            description: response.data.weather[0]. description
        });
    }

    function handleSubmit(event){
        event.preventDefault();
        let apiKey = `9f9579090daa27d42e77fa3c3fec2fd7`;
        let apiURL = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
        axios.get(apiURL).then(displayWeather);
        //make an api call
        //update the weather UI
    }

    function updateCity(event) {
        setCity(event.target.value);//1. have access to a city
    }

    let form = (
        <form onSubmit={handleSubmit}>
                <input type="search" placeholder='Enter a city..' onChange={updateCity} />
                <input type="submit" value="Search" />
            </form>
    )

    if (loaded === true) {
        return (<div>
            {form}
            <ul>
                <li>Temperature: {Math.round(weather.temperature)}ÂºC</li>
                <li>Description: {weather.description}</li>
                <li>Humidity: {weather.humidity}%</li>
                <li>Wind: {weather.wind}km/h</li>
                <li><img src={weather.icon} alt={weather.description} /></li>
            </ul>
        </div>)
    } else {
    return form;
    }
}

export default WeatherSearch;
